repositories {
    mavenCentral()
    maven {
        url "https://repo.repsy.io/mvn/videofirst/vfa"
    }
}

apply plugin: "java"
apply plugin: "idea"

sourceCompatibility = 1.8
targetCompatibility = 1.8

group = "test-project"
version = "0.1"

dependencies {
    testAnnotationProcessor "io.videofirst.vfa:core:$vfaVersion"
    testImplementation "io.videofirst.vfa:core:$vfaVersion"
    testImplementation "io.videofirst.vfa.actions:web-selenide:$vfaVersion"
}

compileJava.options.encoding = 'UTF-8'

tasks.withType(JavaCompile) {
    options.encoding = 'UTF-8'
}

test {
    useJUnitPlatform()
    systemProperty "file.encoding", "utf-8"
    testLogging {
        showStandardStreams = true
    }
}
